mapper.insert:
replace INTO `gmair_airquality`.`province_airquality`
(province_id, aqi_index, pm_2_5, block_flag, create_time)
VALUES
(
#{provinceId}, #{aqi}, #{pm2_5}, #{blockFlag}, #{createAt}
)

replace INTO `gmair_airquality`.`province_airquality`
(province_id, aqi_index, pm_2_5, block_flag, create_time)
VALUES

    (
    #{airQuality.provinceId}, #{airQuality.aqi}, #{airQuality.pm2_5}, #{airQuality.blockFlag}, #{airQuality.createAt}
    )

mapper.select:
select
province_id, pm_2_5, aqi_index
FROM `gmair_airquality`.`province_airquality`
WHERE 1 = 1

    and province_id = #{provinceId}


    and block_flag = #{blockFlag}
