mapper.select:
SELECT admin_id, admin_email, admin_name, admin_password, block_flag, create_time
FROM gmair_admininfo.admin_info
WHERE 1 = 1

    AND admin_id = #{adminId}


    AND admin_email = #{email}


    AND admin_name = #{username}


    AND admin_password = #{password}


    AND block_flag = #{blockFlag}

mapper.update:
UPDATE gmair_admininfo.admin_info


        admin_name = #{username},


        admin_password = #{passowrd},


        block_flag = #{blockFlag},

    admin_id = #{adminId}

WHERE admin_id = #{adminId}
