mapper.select:
SELECT admin_id, admin_email, admin_name, admin_password, admin_role, block_flag, create_time
FROM gmair_admininfo.admin_info
WHERE 1 = 1

    AND admin_id = #{adminId}


    AND admin_email = #{email}


    AND admin_name = #{username}


    AND admin_password = #{password}


    AND admin_role = #{role}


    AND block_flag = #{blockFlag}


SELECT admin_id, admin_email, admin_name, create_time
FROM gmair_admininfo.admin_info
WHERE block_flag = false

    AND admin_name = #{name}

ORDER BY create_time DESC LIMIT #{offset}, #{pageSize}
