mapper.select:
SELECT phone_id, phone_number, mobile_preferred, block_flag, create_time
FROM gmair_userinfo.consumer_phone
WHERE 1 = 1

    AND consumer_id = #{consumerId}


    AND phone_number #{number}

mapper.update:
UPDATE gmair_userinfo.consumer_phone


        mobile_preferred = #{preferred},


        block_flag = #{blockFlag},

    phone_id = #{phoneId}

WHERE phone_id = #{phoneId}
