mapper.select:
SELECT district_id, city_id, district_name, block_flag, create_time
FROM gmair_location.district_list
WHERE 1 = 1

    AND district_id = #{districtId}


    AND city_id = #{cityId}


    AND district_name = #{districtName}


    AND block_flag = #{blockFlag}

ORDER BY create_time DESC

mapper.update:
UPDATE gmair_location.district_list


        city_id = #{cityId},


        district_name = {districtName},


        block_flag = #{blockFlag},

    district_id = #{districtId}

WHERE  district_id = #{districtId}
