mapper.select:
SELECT province_id, province_name, province_pinyin, block_flag, create_time
FROM gmair_location.province_list
WHERE 1 = 1

    AND province_id = #{provinceId}


    AND province_name = #{provinceName}


    AND province_pinyin = #{provincePinyin}


    AND block_flag = #{blockFlag}

ORDER BY create_time DESC

mapper.update:
UPDATE gmair_location.province_list


        province_name = #{provinceName},


        province_pinyin = #{provincePinyin},


        block_flag = #{blockFlag},

    province_id = #{provinceId}

WHERE province_id = #{provinceId}
