mapper.select:
select bill_id, order_id, order_price, actual_price, bill_status,channel_id,account_id, block_flag, create_time
from gmair_bill.bill_info
where 1 = 1

    AND bill_id = #{billId}


    AND order_id = #{orderId}


    AND order_price = #{orderPrice}


    AND actual_price = #{actualPrice}


    AND account_id = #{accountId}


    AND channel_id = #{channelId}


    AND bill_status = #{status}


    AND block_flag = #{blockFlag}

ORDER BY create_time ASC

mapper.update:
update gmair_bill.bill_info


        order_status = #{status, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}

    bill_id = #{billId}

where bill_id = #{billId}
