mapper.select:
SELECT status_id, machine_id, average_temp, max_temp, min_temp, block_flag, create_time
FROM gmair_data_analysis.temp_daily
WHERE 1 = 1

    AND status_id = #{statusId}


    AND machine_id = #{machineId}


    AND create_time > #{createTimeGT}


    AND create_time >= #{createTimeGTE}



        AND create_time < #{createTimeLT}




        AND create_time <= #{createTimeLTE}


order by create_time asc
