mapper.select:
select order_id, order_no, consignee, phone, address, province, city, district, total_price, real_pay, description, order_status, buy_machine, machine_orderNo, block_flag, create_time
from gmair_drift.drift_order
where 1 = 1

    AND order_id = #{orderId}


    AND order_no = #{orderNo}


    AND phone = #{phone}


    AND city like #{cityName}
    OR district like #{cityName}


    AND province like #{provinceName}


    AND create_time >= #{startTime}



        AND create_time <= #{endTime}



    AND order_status = #{status}


    AND block_flag = #{blockFlag}

ORDER BY create_time ASC
