mapper.select:
select address_id, consumer_id, consignee, province, city, district, address_detail, phone, default_address, block_flag, create_time
from gmair_drift.drift_address
where 1 = 1

    and address_id = #{addressId}


    and consumer_id = #{consumerId}


    and consignee = #{consignee}


    and province = #{province}


    and city = #{city}


    and district = #{district}


    and address_detail = #{addressDetail}


    and phone = #{phone}


    and default_address = #{defaultAddress}


    and block_flag = #{blockFlag}

order by create_time desc

mapper.update:
update gmair_drift.drift_address
SET block_flag = TRUE
where address_id = #{addressId}
