mapper.select:
select attach_id, equip_id, attach_name, set_meal, attach_single, attach_price, block_flag, create_time
from gmair_drift.attachment
where 1 = 1

    and attach_id = #{attachId}


    and equip_id = #{equipId}


    and attach_name = #{attachName}


    and set_meal = #{meal}


    and attach_single = #{attachSingle}


    and attach_price = #{attachPrice}


    and block_flag = #{blockFlag}

order by create_time desc

mapper.update:
update gmair_drift.attachment


        equip_id = #{equipId},


        attach_name = #{attachName},


        attach_price = #{attachPrice},

    attach_id = #{attachId}

where attach_id = #{attachId}
