mapper.select:
select cancel_id, order_id, open_id, price, is_finish, block_flag, create_time
from gmair_drift.drift_cancel_order
where 1 = 1

    and cancel_id = #{cancelId}


    and order_id = #{orderId}


    and open_id = #{openId}


    and price = #{price}


    and is_finish = #{isFinish}


    and block_flag = #{blockFlag}

order by create_time asc

mapper.update:
update gmair_drift.drift_cancel_order


        consignee = #{consignee},


        block_flag = #{blockFlag}


where cancel_id = #{cancelId}
