mapper.select:
select order_id, express_id, company, express_num, express_status, block_flag, create_time
from gmair_drift.order_express
where 1 = 1

    AND order_id = #{orderId}


    AND express_id = #{expressId}


    AND company = #{company}


    AND express_status = #{status}


    AND express_num = #{expressNum}


    and block_flag = #{blockFlag}

ORDER BY create_time desc
