mapper.select:
select notification_id, activity_id, notification_context, block_flag, create_time
from gmair_drift.activity_notification
where 1 = 1

    and notification_id = #{notificationId}


    and activity_id = #{activityId}


    and notification_context = #{context}


    and block_flag = #{blockFlag}

order by create_time desc

mapper.update:
update gmair_drift.activity_notification


        notification_context = #{context},


        block_flag = #{blockFlag},

    notification_id = #{notificationId}

where notification_id = #{notificationId}
