mapper.select:
select repository_id, equip_id, pool_size, block_flag, create_time
from gmair_drift.drift_repository
where 1 = 1

    and repository_id = #{repositoryId}


    and equip_id = #{equipId}


    and pool_size = #{poolSize}


    and block_flag = #{blockFlag}

order by create_time desc

mapper.update:
UPDATE gmair_drift.drift_repository


        pool_size = #{poolSize},


        block_flag = #{blockFlag},

    repository_id = #{repositoryId}

where repository_id = #{repositoryId}
