mapper.select:
SELECT express_id, order_id, company_id, express_no, express_status, deliver_time, receive_time, block_flag, create_time
FROM gmair_express.order_express
WHERE 1 = 1

    AND express_id = #{expressId}


    AND order_id = #{orderId}


    AND company_id= #{companyId}


    AND express_no= #{expressNo}


    AND express_status < #{expressStatus}


    AND block_flag = #{blockFlag}
