mapper.select:
SELECT express_id, parent_express, express_no, code_value, express_status, parcel_type, block_flag, create_time
FROM gmair_express.parcel_express
WHERE 1 = 1

    AND express_id = #{expressId}


    AND express_no = #{expressNo}


    AND code_value = #{codeValue}


    AND express_status < #{expressStatus}


    AND block_flag = #{blockFlag}

mapper.update:
UPDATE gmair_express.parcel_express
SET
express_status = #{expressStatus}
WHERE express_id IN

    #{expressParcel.expressId}
