mapper.select:
SELECT case_id,case_holder,equipment_id,check_duration,check_date,case_city_id,case_city_name,case_location,check_trace,case_status,video_id,block_flag,create_time
FROM gmair_formaldehyde_detection.case_profile
WHERE 1 = 1

    AND case_id = #{caseId}


    AND case_holder = #{caseHolder}


    AND check_duration = #{checkDuration}


    AND check_date = #{checkDate}


    AND case_city_id = #{cityId}


    AND case_status = #{caseStatus}


    AND video_id = #{videoId}


    AND block_flag = #{blockFlag}

mapper.update:
UPDATE gmair_formaldehyde_detection.case_profile


        case_status = #{caseStatus, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler},


        block_flag = #{blockFlag},

    case_id = #{caseId}

WHERE case_id = #{caseId}
