mapper.select:
SELECT feedback_id, code_value, member_phone, feedback_content, block_flag, create_time
FROM gmair_install.install_feedback
WHERE 1 = 1

    AND feedback_id = #{feedbackId}


    AND code_value = #{qrcode}


    AND member_phone = #{memberPhone}


    AND block_flag = #{blockFlag}

mapper.update:
UPDATE gmair_install.install_feedback


        block_flag = #{blockFlag},

    feedback_id = #{feedbackId}

WHERE feedback_id = #{feedbackId}
