mapper.insert:
INSERT INTO gmair_install.team_member(member_id, team_id, member_phone, member_name, block_flag, create_time)
VALUES(
  #{memberId},#{teamId},#{memberPhone},#{memberName},#{blockFlag},#{createAt}
)

mapper.select:
SELECT member_id,team_id,member_phone,member_name,block_flag,create_time
FROM gmair_install.team_member
WHERE 1 = 1

    AND member_id = #{memberId}


    AND team_id = #{teamId}


    AND member_phone = #{memberPhone}


    AND member_name = #{memberName}


    AND block_flag = #{blockFlag}

mapper.update:
UPDATE gmair_install.team_member


        member_name = #{memberName},


        member_phone = #{memberPhone},


        block_flag = #{blockFlag},

    member_id = #{memberId}

WHERE member_id = #{memberId}
