mapper.select:
        SELECT
        assign_id,
        code_value,
        assign_detail,
        assign_status,
        assign_date,
        block_flag,
        consumer_consignee,
        consumer_phone,
        consumer_address,
        assign_source,
        team_id,
        team_name,
        member_id,
        member_name,
        assign_type,
        create_time
        FROM gmair_install.assign_report_view
        WHERE 1 = 1

            AND assign_id = #{assignId}


            AND assign_detail = #{assignDetail}


            AND assign_status = #{assignStatus}


            AND consumer_consignee = #{consumerConsignee}


            AND team_id = #{teamId}


            AND team_name = #{teamName}


            AND member_id = #{memberId}


            AND member_name = #{memberName}


            AND assign_type = #{assignType}


            AND team_id IN

                #{item}




				and	DATE_FORMAT(assign_date, '%Y-%m-%d')>= DATE_FORMAT(#{beginTime}, '%Y-%m-%d')




				and	DATE_FORMAT(assign_date, '%Y-%m-%d')<= DATE_FORMAT(#{endTime}, '%Y-%m-%d')



            AND block_flag = #{blockFlag}


            ORDER BY create_time desc


            ORDER BY assign_date desc


            LIMIT #{start},#{length}

        SELECT count(*)
        FROM gmair_install.assign_report_view
        WHERE 1 = 1

            AND assign_id = #{assignId}


            AND assign_detail = #{assignDetail}


            AND assign_status = #{assignStatus}


            AND consumer_consignee = #{consumerConsignee}


            AND team_id = #{teamId}


            AND team_name = #{teamName}


            AND member_id = #{memberId}


            AND member_name = #{memberName}


            AND assign_type = #{assignType}


            AND team_id IN

                #{item}




				and	DATE_FORMAT(assign_date, '%Y-%m-%d')>= DATE_FORMAT(#{beginTime}, '%Y-%m-%d')




				and	DATE_FORMAT(assign_date, '%Y-%m-%d')<= DATE_FORMAT(#{endTime}, '%Y-%m-%d')



            AND block_flag = #{blockFlag}


            ORDER BY create_time desc


            ORDER BY assign_date desc
