mapper.select:
SELECT assign_id, code_serial, block_flag, create_time
FROM gmair_install.assign_code
WHERE 1 = 1

    AND assign_id = #{assignId}


    AND code_serial = #{codeSerial}


    AND block_flag = #{blockFlag}

ORDER BY create_time ASC

mapper.update:
UPDATE gmair_install.assign_code


        assignId = #{assign_id},


        codeSerial = #{code_serial},


        block_flag = #{blockFlag},

    assign_id = #{assignId}

WHERE assign_id = #{assignId}

UPDATE gmair_install.assign_code
SET block_flag = TRUE
WHERE assign_id = #{assignId}
