mapper.select:
SELECT snapshot_id, assign_id, code_value, picture_path, wifi_configured, install_method, description,
block_flag, create_time
FROM gmair_install.assign_snapshot
WHERE 1 = 1

    AND snapshot_id = #{snapshotid}


    AND assign_id = #{assignId}


    AND code_value = #{codeValue}


    AND picture_path = #{picturePath}


    AND wifi_configured = #{wifiConfigured}


    AND install_method = #{installMethod}


    AND block_flag = #{blockFlag}

mapper.update:
UPDATE gmair_install.assign_snapshot
SET block_flag = TRUE
WHERE snapshot_id = #{snapshotId}
