mapper.select:
SELECT snapshot_id, assign_id, code_value, picture_path, description, waybill_number,
block_flag, create_time
FROM gmair_install.disassemble_snapshot
WHERE 1 = 1

    AND snapshot_id = #{snapshotid}


    AND assign_id = #{assignId}


    AND code_value = #{codeValue}


    AND picture_path = #{picturePath}


    AND waybill_number = #{wayBillNumber}


    AND block_flag = #{blockFlag}

mapper.update:
UPDATE gmair_install.disassemble_snapshot
SET block_flag = TRUE
WHERE snapshot_id = #{snapshotId}
