mapper.select:
select order_id, express_id, company, express_num, express_status, create_time
from gmair_install.order_express
where 1 = 1

    AND order_id = #{orderId}


    AND express_id = #{expressId}


    AND company = #{company}


    AND express_num = #{expressNum}


    AND express_status = #{status}

ORDER BY create_time desc
