mapper.select:
SELECT team_id, team_name, team_area, team_description, block_flag, create_time
FROM gmair_install.install_team
WHERE 1 = 1

    AND team_id = #{teamId}


    AND team_name = #{teamName}


    AND team_area = #{teamArea}


    AND block_flag = #{blockFlag}

ORDER BY create_time ASC

mapper.update:
UPDATE gmair_install.install_team
SET block_flag = TRUE
WHERE team_id = #{teamId}
