mapper.select:
SELECT bind_id, consumer_id, bind_name, code_value, ownership, block_flag, create_time
FROM gmair_machine.consumer_code_bind
WHERE 1 = 1

    AND bind_id = #{bindId}


    AND consumer_id = #{consumerId}


    AND bind_name = #{bindName}


    AND code_value = #{codeValue}


    AND ownership = #{ownership}


    AND block_flag = #{blockFlag}


    AND create_time > #{createTimeGT}


    AND create_time >= #{createTimeGTE}



        AND create_time < #{createTimeLT}




        AND create_time <= #{createTimeLTE}


order by create_time desc

mapper.update:
UPDATE gmair_machine.consumer_code_bind
SET block_flag = 1
WHERE bind_id = #{bindId}
