mapper.select:
SELECT option_id, option_name, option_component, model_id, block_flag, create_time
FROM gmair_machine.control_option_view
WHERE 1 = 1

    AND option_id = #{controlId}


    AND option_name = #{optionName}


    AND option_component = #{optionComponent}


    AND model_id = #{modelId}


    AND block_flag = #{blockFlag}

ORDER BY create_time DESC

SELECT value_id, action_name, action_operator, block_flag, create_time
FROM gmair_machine.control_option_action
WHERE 1 = 1

    AND value_id = #{valueId}


    AND action_name = #{actionName}


    AND action_operator = #{actionOperator}


    AND block_flag = #{blockFlag}

ORDER BY create_time DESC
