mapper.select:
SELECT machine_id,light_status,block_flag,create_time
FROM gmair_machine.filter_light
WHERE 1 = 1

    AND machine_id = #{machineId}


    AND light_status = #{lightStatus}


    AND block_flag = #{blockFlag}

mapper.update:
update gmair_machine.filter_light


        light_status = #{lightStatus},


        create_time = #{createAt},

    machine_id = #{machineId}

where machine_id = #{machineId}
