mapper.select:
SELECT machine_id, block_flag, create_time
FROM gmair_machine.idle_machine
WHERE 1 = 1

    AND machine_id = #{machineId}


    AND block_flag = #{blockFlag}

ORDER BY creata_time DESC

mapper.update:
UPDATE gmair_machine.idle_machine
SET block_flag = 1
WHERE machine_id = #{machineId}
