mapper.select:
SELECT setting_id, consumer_id, setting_name, code_value, light_value
FROM gmair_machine.light_setting_view
WHERE 1 = 1

    AND setting_id = #{settingId}


    AND consumer_id = #{consumerId}


    AND setting_name = #{settingName}


    AND code_value = #{codeValue}


    AND light_value = #{lightValue}

mapper.update:
UPDATE gmair_machine.light_setting
SET
light_value = #{lightValue}
WHERE setting_id = #{settingId}
