mapper.select:
SELECT location_id, city_id, code_value, block_flag, create_time
FROM gmair_machine.machine_default_location
WHERE 1 = 1

    AND location_id = #{locationId}


    AND city_id = #{cityId}


    AND code_value = #{codeValue}


    AND block_flag = #{blockFlag}

mapper.update:
UPDATE gmair_machine.machine_default_location


        city_id = #{cityId},


        block_flag = #{blockFlag},

    code_value = #{codeValue}

WHERE code_value = #{codeValue}
