mapper.select:
SELECT qr_code, turbo_volume_status, block_flag, create_time
FROM gmair_machine.machine_turbo_volume
WHERE 1 = 1

    AND qr_code = #{qrcode}


    AND turbo_volume_status = #{turboVolumeStatus}


    AND block_flag = #{blockFlag}

mapper.update:
UPDATE gmair_machine.machine_turbo_volume


        turbo_volume_status = #{turboVolumeStatus},


        block_flag = #{blockFlag},

    qr_code = #{qrcode}

WHERE qr_code = #{qrcode}
