mapper.select:
SELECT bind_id, code_value, machine_id
FROM `gmair_machine`.`code_machine_bind`
WHERE 1 = 1

    AND bind_id = #{bindId}


    AND code_value = #{codeValue}


    AND machine_id = #{machineId}


    AND block_flag = #{blockFlag}

ORDER BY create_time DESC

mapper.update:
UPDATE gmair_machine.code_machine_bind


        code_value = #{codeValue},


        machine_id = #{machineId},


        block_flag = #{blockFlag},

    code_value = #{codeValue}

WHERE code_value = #{codeValue}
