mapper.select:
SELECT config_id,model_id,min_volume,max_volume,block_flag,create_time
FROM gmair_machine.model_volume_config
WHERE 1 = 1

    AND config_id = #{configId}


    AND model_id = #{modelId}


    AND min_volume = #{minVolume}


    AND max_volume = #{maxVolume}

mapper.update:
UPDATE gmair_machine.model_volume_config


        model_id = #{modelId},


        min_volume = #{minVolume},


        max_volume = #{maxVolume},

model_id = #{modelId}

WHERE model_id = #{modelId}
