mapper.select:
SELECT latest_id,machine_id,pm2_5,index_hour,block_flag,create_time
FROM gmair_machine.out_pm2_5_hourly
WHERE 1 = 1

    AND latest_id = #{latestId}


    AND machine_id = #{machineId}


    AND pm2_5 = #{pm2_5}


    AND create_time >= #{lastHour}



    AND create_time < #{currentHour}



    AND create_time > #{createTimeGT}



        AND create_time <= #{createTimeLTE}


order by create_time desc

mapper.update:
UPDATE gmair_machine.out_pm2_5_hourly


        machine_id = #{machineId},


        pm2_5 = #{pm2_5},


        create_time = #{createAt},

    machine_id = #{machineId}

WHERE machine_id = #{machineId}
