mapper.select:
SELECT boundary_id,model_id,pm2_5,block_flag,create_time
FROM gmair_machine.pm2_5_boundary
WHERE 1 = 1

    AND boundary_id = #{boundaryId}


    AND model_id = #{modelId}


    AND pm2_5 = #{pm2_5}

mapper.update:
UPDATE gmair_machine.pm2_5_boundary


        model_id = #{modelId},


        pm2_5 = #{pm2_5},

model_id = #{modelId}

WHERE model_id = #{modelId}
