mapper.select:
SELECT latest_id,machine_id,pm2_5,block_flag,create_time
FROM gmair_machine.pm2_5_latest
WHERE 1 = 1

    AND latest_id = #{latestId}


    AND machine_id = #{machineId}


    AND pm2_5 = #{pm2_5}

mapper.update:
UPDATE gmair_machine.pm2_5_latest


        machine_id = #{machineId},


        pm2_5 = #{pm2_5},

    machine_id = #{machineId}

WHERE machine_id = #{machineId}
