mapper.select:
SELECT setting_id, consumer_id, setting_name, code_value, floor_pm2_5, upper_pm2_5, speed_value
FROM gmair_machine.volume_setting_view
WHERE 1 = 1

    AND setting_id = #{settingId}


    AND consumer_id = #{consumerId}


    AND setting_name = #{settingName}


    AND code_value = #{codeValue}


    AND floor_pm2_5 = #{floorPm2_5}


    AND upper_pm2_5 = #{upperPm2_5}


    AND speed_value = #{speedValue}

mapper.update:
UPDATE gmair_machine.volume_setting
SET
floor_pm2_5 = #{floorPm2_5},
upper_pm2_5 = #{upperPm2_5},
speed_value = #{speedValue}
WHERE setting_id = #{settingId}
