mapper.select:
SELECT template_id, message_catalog, message_content, block_flag, create_time
FROM gmair_message.message_template
WHERE 1 = 1

    AND template_id = #{templateId}


    AND message_catalog = #{catalog}


    AND block_flag = #{blockFlag}

mapper.update:
UPDATE gmair_message.message_template


        message_content = #{message},

    template_id = #{templateId}

WHERE template_id = #{templateId}
