mapper.update:
update gmair_mqtt_management.attribute


        description = #{description},


        required = #{required},

    attribute_id = #{attributeId}

where attribute_id = #{attributeId}

mapper.select:
select attribute_id, name, description, required, block_flag, create_time
from gmair_mqtt_management.attribute
where attribute_id = #{attributeId}

select attribute_id, name, description, required, block_flag, create_time
from gmair_mqtt_management.attribute
where 1 = 1

    and attribute_id = #{attributeId}


    and name = #{name}


    and description = #{description}


    and required = #{required}


    and block_flag = #{blockFlag}

order by create_time desc
