mapper.select:
select firmware_id, firmware_version, firmware_link, block_flag, create_time
from gmair_mqtt.mqtt_firmware
where 1 = 1

    and firmware_id = #{firmwareId}


    and firmware_version = #{firmwareVersion}


    and firmware_link = #{firmwareLink}


    and block_flag = #{blockFlag}

order by create_time desc

mapper.update:
update gmair_mqtt.mqtt_firmware


        firmware_version = #{firmwareVersion},


        firmware_link = #{firmwareLink},

    firmware_id = #{firmwareId}

where firmware_id = #{firmwareId}
