mapper.update:
update gmair_mqtt_management.machine_alert
set alert_status = #{alertStatus}
where alert_id = #{alertId}

update gmair_mqtt_management.machine_alert
set alert_status = #{alertStatus}
where alert_id in

    #{MachineAlert.alertId}

mapper.select:
select alert_id, machine_id, alert_code, alert_msg, alert_status, block_flag, create_time
from gmair_mqtt.machine_alert
where 1 = 1

    and alert_id = #{alertId}


    and machine_id = #{machineId}


    and alert_code = #{alertCode}


    and alert_msg = #{alertMsg}


    and alert_status = #{alertStatus}


    and block_flag = #{blockFlag}

order by create_time desc
