mapper.update:
update gmair_mqtt_management.mqtt_topic


        topic_detail = #{topicDetail},


        topic_description = #{topicDescription},

    topic_id = #{topicId}

where topic_id = #{topicId}

mapper.select:
select topic_id, topic_detail, topic_description, block_flag, create_time
from gmair_mqtt_management.mqtt_topic
where 1 = 1

    and topic_id = #{topicId}


    and topic_detail = #{topicDetail}


    and topic_description = #{topicDescription}


    and block_flag = #{blockFlag}

order by create_time desc
