mapper.select:
select

from unified_trade
where tid = #{tid,jdbcType=VARCHAR}
and trade_platform = #{platform,jdbcType=INTEGER}

select

from unified_trade
where tid = #{tid,jdbcType=VARCHAR}
and shop_id = #{shopId,jdbcType=INTEGER}

select

from unified_trade


        and tid = #{tid,jdbcType=VARCHAR}


        and shop_id = #{shopId,jdbcType=VARCHAR}


        and trade_platform = #{platform,jdbcType=INTEGER}



        and pay_time >= #{beginTime,jdbcType=TIMESTAMP}




        and pay_time <= #{endTime,jdbcType=TIMESTAMP}



        and phone = #{phone,jdbcType=VARCHAR}


order by pay_time
