mapper.select:
SELECT item_id, item_name, item_price, quantity FROM `gmair_order`.`order_item`
WHERE
order_item.order_id = #{order_id}

SELECT order_id, order_no, consignee, phone, address, province, city, district, latitude, longitude, total_price, order_channel,
description, order_status, block_flag, create_time
FROM gmair_order.platform_order
WHERE 1 = 1

    AND order_id = #{orderId}


    AND order_no = #{orderNo}


    AND phone = #{phone}


    AND city like #{cityName}


    AND create_time >= #{startTime}



        AND create_time <= #{endTime}



    AND order_status = #{status}


    AND block_flag = #{blockFlag}


    AND (province IS NULL OR city IS NULL)

ORDER BY create_time ASC

mapper.update:
update gmair_order.platform_order
set

    order_status = #{status},

order_id = #{orderId}
where order_id = #{orderId}
