mapper.select:
SELECT item_id, item_name, quantity, item_price, block_flag, create_time
FROM gmair_order.order_item
WHERE 1 = 1

    AND item_id = #{itemId}


    AND block_flag = #{blockFlag}

mapper.update:
UPDATE gmair_order.order_item


        item_name = #{itemName},


        item_price = #{itemPrice},


        block_flag = #{blockFlag},

    item_id = #{itemId}

WHERE item_id = #{itemId}
