mapper.select:
select trade_id, out_trade_no, trade_description, nonce_str, total_fee,spbill_create_ip,trade_type, openid, start_time, end_time, trade_status
from gmair_payment.trade
where 1 = 1

    AND trade_id = #{tradeId}


    AND out_trade_no = #{orderId}

mapper.update:
update gmair_payment.trade


        trade_status = #{tradeState, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


        end_time = #{tradeEndTime}


where trade_id = #{tradeId}
