mapper.insert:
INSERT INTO gmair_resource.tempfile_location(file_id, file_url, actual_path, filename,md5, block_flag, create_time)
VALUES(
  #{fileId},#{fileUrl},#{actualPath},#{fileName},#{md5},#{blockFlag},#{createAt}
)

mapper.select:
SELECT file_id,file_url,actual_path,filename,md5,block_flag,create_time
FROM gmair_resource.tempfile_location
WHERE 1 = 1

    AND file_id = #{fileId}


    AND file_url = #{fileUrl}


    AND actual_path = #{actualPath}


    AND filename = #{fileName}


    AND md5 = #{md5}


    AND block_flag = #{blockFlag}


    AND DATEDIFF(CURRENT_DATE ,create_time) > 7

mapper.delete:
DELETE FROM gmair_resource.tempfile_location
WHERE 0 = 1

    OR file_id = #{fileId}


    OR file_url = #{fileUrl}


    OR DATEDIFF(CURRENT_DATE ,create_time) > 7
