mapper.select:
SELECT template_id, article_title, description_type, description_content, picture_url, article_url, block_flag, create_time
FROM gmair_wechat.article_template
WHERE 1 = 1

    AND template_id = #{templateId}


    AND article_title = #{articleTitle}


    AND description_type = #{descriptionType}


    AND description_content = #{descriptionContent}


    AND picture_url = #{pictureUrl}


    AND article_url = #{articleUrl}


    AND block_flag = #{blockFlag}

ORDER BY create_time DESC
